var FormValidation = function() {
        var r = function() {
            var e = $("#formProduct"),
                id = $("#productId").val(),
                mainImage = $("#checkMainImage").val(),
                r = $(".alert-danger", e),
                i = $(".alert-success", e);

            toastr.options = {
                "closeButton": true,
                "debug": false,
                "newestOnTop": false,
                "progressBar": false,
                "positionClass": "toast-top-right",
                "preventDuplicates": false,
                "showDuration": "300",
                "hideDuration": "1000",
                "timeOut": "5000",
                "extendedTimeOut": "1000",
                "showEasing": "swing",
                "hideEasing": "linear",
                "showMethod": "fadeIn",
                "hideMethod": "fadeOut"
            };

            e.validate({
                errorElement: "span",
                errorClass: "help-block help-block-error",
                focusInvalid: !1,
                ignore: "",
                rules: {
                    "formProduct[name]": {
                        required: true
                    },
                    "formProduct[mainImage]": {
                        required: mainImage === 0 ? true : false
                    },
                    "formProduct[price]": {
                        required: true
                    },
                    "formProduct[salePrice]": {
                        required: true
                    },
                    "formProduct[description]": {
                        required: true
                    }
                },
                invalidHandler: function(e, t) {
                    i.hide(), r.show(), App.scrollTo(r, -200)
                },
                errorPlacement: function(e, r) {
                    var i = $(r).parent(".input-icon").children("i");
                    i.removeClass("fa-check").addClass("fa-warning"), i.attr("data-original-title", e.text()).tooltip({
                        container: "body"
                    })
                },
                highlight: function(e) {
                    $(e).closest(".form-group").removeClass("has-success").addClass("has-error")
                },
                unhighlight: function(e) {},
                success: function(e, r) {
                    var i = $(r).parent(".input-icon").children("i");
                    $(r).closest(".form-group").removeClass("has-error").addClass("has-success"), i.removeClass("fa-warning").addClass("fa-check")
                },
                submitHandler: function(e) {
                    r.hide();
                    saveContent();
                    return false;
                }
            })
        };

        var options = {
            // target:        '#formProduct',   // target element(s) to be updated with server response
            beforeSubmit:  showRequest,  // pre-submit callback
            success:       showResponse  // post-submit callback
        };

        function showRequest(formData, jqForm, options) {
            return true;
        }

        function showResponse(responseText, statusText, xhr, $form)  {
            toastr["info"]("Update Product Information Success!");
        }

        var saveContent = function() {
            $("#formProduct").ajaxSubmit(options);
            return false;
        };
    return {
        init: function() {
            r()
        }
    }
}();
jQuery(document).ready(function() {
    FormValidation.init()
});